from bisect import insort


class ExamRoom:

    def __init__(self, n: int):
        self.state = []
        self.n = n

    def seat(self) -> int:

        if not self.state:
            self.state.append(0)
            return 0

        pos = 0
        prev = self.state[0]
        max_dist = self.state[0]
        for curr in self.state[1:]:
            dist = (curr - prev) // 2
            if max_dist < dist:
                max_dist = dist
                pos = prev + dist
            prev = curr

        if max_dist < self.n - prev - 1:
            max_dist = self.n - prev - 1
            pos = self.n -1

        insort(self.state, pos)
        return pos

    def leave(self, p: int) -> None:
        self.state.remove(p)


